				S0L1C1TUD3S
				 ) 
		) CE
		GROUP BY CE.IDSOLICITUD, CE.NUMEROCONTROLCONSULTA    
	) O 
	ON E.IDSOLICITUD = O.IDSOLICITUD AND E.NUMEROCONTROLCONSULTA = O.NUMEROCONTROLCONSULTA 
	LEFT JOIN 
		(
		   SELECT T1.IDSOLICITUD, T1.NUMEROCONTROLCONSULTA, 
			  T2.CODIGOERROR,
			  NULL AS CODIGORAZON,
			  T2.CODIGOSCORE,
			  T2.NOMBRESCORE,
			  T2.VALORSCORE,
			  ROW_NUMBER() OVER(PARTITION BY T1.IDSOLICITUD, T1.NUMEROCONTROLCONSULTA 
							 ORDER BY CODIGOSCORE) ORDEN
		   FROM TB_BCRES_ENCABEZADO T1
						  JOIN TB_BCRES_SCOREBC T2 ON T1.IDSOLICITUD = T2.IDSOLICITUD AND T1.NUMEROCONTROLCONSULTA = T2.NUMEROCONTROLCONSULTA
		   WHERE T1.IDSOLICITUD IN (
		   		S0L1C1TUD3S
				)
		) BC 
		ON E.IDSOLICITUD = BC.IDSOLICITUD AND E.NUMEROCONTROLCONSULTA = BC.NUMEROCONTROLCONSULTA 
